import React, { useState, useEffect } from 'react'
import { useHistory, useLocation } from 'react-router-dom'
import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow,
  CFormGroup,
  CInput,
  CLabel,
  CPagination,
  CButton
} from '@coreui/react'

 import usersData from '../../views/users/UsersData'

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const DashboardAddUser = () => {
//   const history = useHistory()
//   const queryPage = useLocation().search.match(/page=([0-9]+)/, '')
//   const currentPage = Number(queryPage && queryPage[1] ? queryPage[1] : 1)
//   const [page, setPage] = useState(currentPage)

//   const pageChange = newPage => {
//     currentPage !== newPage && history.push(`/users?page=${newPage}`)
//   }

  useEffect(() => {
    // currentPage !== page && setPage(currentPage)
  }, [])

  return (
    <CRow>
      <CCol xl={6}>
        <CCard>
          <CCardHeader>
          Add User
          </CCardHeader>
          <CCardBody>
          <CRow>
                <CCol xs="12">
                  <CFormGroup>
                    <CLabel htmlFor="name">Name</CLabel>
                    <CInput id="name" placeholder="Enter your name" required />
                  </CFormGroup>
                </CCol>
              </CRow>
              <CRow>
                <CCol xs="12">
                  <CFormGroup>
                    <CLabel htmlFor="ccnumber">Email</CLabel>
                    <CInput id="ccnumber" type="email" placeholder="Enter email here.." required />
                  </CFormGroup>
                </CCol>
              </CRow>
              <CRow className="text-center">
                  <CButton className="btn btn-primary md">Add</CButton>
              </CRow>
         
          </CCardBody>
        </CCard>
      </CCol>


      <CCol xl={6}>
        <CCard>
          <CCardHeader>
            Users
            <small className="text-muted"> example</small>
          </CCardHeader>
          <CCardBody>
          <CDataTable
            items={usersData}
            fields={[
              { key: 'name', _classes: 'font-weight-bold' },
              'registered', 'role', 'status'
            ]}
            hover
            striped
            itemsPerPage={5}
            clickableRows
            onRowClick={(item) => {}}
            scopedSlots = {{
              'status':
                (item)=>(
                  <td>
                    <CBadge color={getBadge(item.status)}>
                      {item.status}
                    </CBadge>
                  </td>
                )
            }}
          />
         
          </CCardBody>
        </CCard>
      </CCol>
    </CRow>
  )
}

export default DashboardAddUser
