import React,{useContext} from 'react'
import ArticleProvider from "../context/ArticleContext"
import {
    CInputGroup,
    CInputGroupPrepend,
    CInputGroupText,
    CInput,
  } from '@coreui/react'
  import CIcon from '@coreui/icons-react'


const ArticleStatus=()=>{
    const {article,updateArticle}=useContext(ArticleProvider.Context)
    return (
    <CInputGroup className={""}>
       <CInputGroupPrepend>
         <CInputGroupText>
           <CIcon name="cil-user" />
         </CInputGroupText>
       </CInputGroupPrepend>
       <CInput type="radio"  placeholder="Title here.." value="published" onChange={(e)=>{
        actionType=="en" ? updateArticle("titleEn", e.target.value):  updateArticle("titleFr", e.target.value)
         console.log("article",article)
         }} />  
         <CInput type="radio"  placeholder="Title here.." value="draft" onChange={(e)=>{
        actionType=="en" ? updateArticle("titleEn", e.target.value):  updateArticle("titleFr", e.target.value)
         console.log("article",article)
         }} />
     </CInputGroup>
   

    )
    

   

}
 export default ArticleStatus
