import React,{useContext,useEffect} from 'react'
import {
    CInputGroup,
    CInputGroupPrepend,
    CInputGroupText,
    CInput,
  } from '@coreui/react'
  import CIcon from '@coreui/icons-react'
//   import ArticleProvider from "../context/ArticleContext"

const SearchInput =({context})=>{
    const { searchPhrase,
        setSearchPhrase,
        searchArticleByPhraseApi
    }=useContext(context)


    useEffect(()=>{

      

    },[])
      
    return (<div className="input-group">
     <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">@</span>
     </div>
     <input type="text"  className="form-control" placeholder="Search here..." onChange={(e)=>setSearchPhrase(e.target.value)} onKeyDown={(e)=>e.keyCode == 13 ?searchArticleByPhraseApi():""} />
     <div className="input-group-prepend">
    <button className="input-group-text" id="basic-addon1" onClick={
        ()=>{
        searchPhrase!=="" && searchArticleByPhraseApi()
        }
        } >Search</button>
     </div>
    </div>)
    

}

export default SearchInput
