import React,{useState,useEffect,useContext} from 'react'
import axios from "axios";
import ArticleProvider from "../context/ArticleContext"


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

const PUBLIC_URL = "http://192.168.43.122:8080/laravel/site40/public/";

const ImageUpload= ({postType,target})=>{

  const {setFeaturedFor,setModal,selectedImageEn,selectedImageFr}=useContext(ArticleProvider.Context)

    //console.log("target",target)

    const triggerSelectInput=(e,input,id)=>{
        e.preventDefault()
        setFeaturedFor(id)
        setModal(true)
    }

    useEffect(()=>{
        const uploadBt=document.getElementsByClassName(target)
        console.log("target",target)
        const uploadInputName="modal-upload-bt"
        //postType=="en" ? "upload_input_en" : "upload_input_fr"
        const uploadInput=document.getElementById(uploadInputName)
        //console.log("elements id is ",uploadBt[0].id)

        document.body.addEventListener("click",(e)=>{
         
          if (e.target.tagName == 'A' && e.target.classList.contains("acpb_featured_en_remove")) {
            e.preventDefault(); 
            alert('Clicked target',e.target.id);
          }
        })

        document.body.addEventListener("click",(e)=>{
         
          if (e.target.tagName == 'A' && e.target.classList.contains("acpb_featured_fr_remove")) {
            e.preventDefault(); 
            alert('Clicked target',e.target.id);
          }
        })

        
        uploadBt[0].addEventListener("click",(e)=>triggerSelectInput(e,uploadInput,uploadBt[0].id))
        uploadBt[1].addEventListener("click",(e)=>triggerSelectInput(e,uploadInput,uploadBt[1].id))
     
    },[])

    

    return postType=="en" ? <img className="img-fluid" src={selectedImageEn} /> : <img className="img-fluid" src={selectedImageFr} />
        
    
}

export default ImageUpload
