import React,{useContext} from 'react';
import { Editor } from '@tinymce/tinymce-react';
import ArticleContextWrapper from "../context/ArticleContext"

const RichText =(actionType)=> {
  const ArticleContext=useContext(ArticleContextWrapper.Context)

  const handleEditorChange = (e) => {
    console.log(
      'Content was updated:',
      e.target.getContent()
    );

    console.log(
      'Action Type:',
      actionType
    );

    ArticleContext.updateArticle("titleEn",e.target.getContent())

    
  }

  
    return (
      <Editor
      apiKey="mymryl9rtn2npjz2vhp3yovdx2of4phahbqil1sq29upca83"
        initialValue="<p>Wow ! ... It Works !!!</p>"
        init={{
          height: 500,
          menubar: false,
          plugins: [
            'advlist autolink lists link image',
            'charmap print preview anchor help',
            'searchreplace visualblocks code',
            'insertdatetime media table paste wordcount'
          ],
          toolbar:
            'undo redo | formatselect | bold italic | \
            alignleft aligncenter alignright | \
            bullist numlist outdent indent | help'
        }}
        onChange={(e)=>handleEditorChange(e)}
      />
    );

}

export default RichText;
