import axios from "axios";


const API_URL = "http://192.168.43.122:8080/laravel/site44/public/api/v1/auth/";

const register = (username, email, password) => {
  return axios.post(API_URL + "signup", {
    username,
    email,
    password,
  });
};

const login = (email, password) => {
  return axios
    .post(API_URL + "login", {
      email,
      password,
    })
    .then((response) => {
        
        //alert("hi there")
        console.log('response',response)
      if (response.data.access_token) {
        console.log("LAC response",response)
          let userData={
              id:response.data.user.id,
              name:response.data.user.name,
              email:response.data.user.email,
              accessToken:response.data.access_token
          }
          
        localStorage.setItem("lac_user", JSON.stringify(userData));
      }
      //console.log(response)
      return response.data;
    })
};

const logout = () => { 
localStorage.removeItem("lac_user");
};

const getCurrentUser = () => {
  return JSON.parse(localStorage.getItem("lac_user"));
};

export default {
  register,
  login,
  logout,
  getCurrentUser,
};
