import React, {useEffect, lazy } from 'react'
import {
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import FormikPostEnglish from "../../formik-wrappers/FormikPostEnglish"
import EditorJS from '@editorjs/editorjs';


const DashboardAddPostTest = () => {


    const editor = new EditorJS({
        autofocus: true,
        holder:'editorjs',
        tools: {
            image: SimpleImage
          },
          data: {
            time: 1552744582955,
            blocks: [
              {
                type: "image",
                data: {
                  url: "https://cdn.pixabay.com/photo/2017/09/01/21/53/blue-2705642_1280.jpg"
                }
              }
            ],
            version: "2.11.10"
          }
      });


      const saveEditor= ()=>{
        const output = document.getElementById('output');
        editor.save().then( savedData => {
            console.log(JSON.stringify(savedData, null, 4));
          })
      }


     return (
    <>
      <CRow>
        <CCol md="9">
          <CCard>
            <CCardHeader>
              English
            </CCardHeader>
            <CCardBody>
            <div id="editorjs"></div>
            <button onClick={()=>saveEditor()}>Save</button>


        </CCardBody>
          </CCard>
        </CCol>
      </CRow>




    </>
  )
}


class SimpleImage {


    constructor({data}){
        this.data = data;
      }

    static get toolbox() {
      return {
        title: 'Image',
        icon: '<svg width="17" height="15" viewBox="0 0 336 276" xmlns="http://www.w3.org/2000/svg"><path d="M291 150V79c0-19-15-34-34-34H79c-19 0-34 15-34 34v42l67-44 81 72 56-29 42 30zm0 52l-43-30-56 30-81-67-66 39v23c0 19 15 34 34 34h178c17 0 31-13 34-29zM79 0h178c44 0 79 35 79 79v118c0 44-35 79-79 79H79c-44 0-79-35-79-79V79C0 35 35 0 79 0z"/></svg>'
      };
    }
  
    render(){
        this.wrapper = document.createElement('div');
        this.wrapper.setAttribute("style","padding: 20px 0;")
        const input = document.createElement('input');
        input.setAttribute("style",`width: 100%;
        padding: 10px;
        border: 1px solid #e4e4e4;
        border-radius: 3px;
        outline: none;
        font-size: 14px;`)
    
        this.wrapper.classList.add('simple-image');
        this.wrapper.appendChild(input);
    
        input.placeholder = 'Paste an image URL...';
        input.value = this.data && this.data.url ? this.data.url : '';

        input.addEventListener('paste', (event) => {
            this._createImage(event.clipboardData.getData('text'));
          });
    
        return this.wrapper;
    }
  
    save(blockContent){
        const image = blockContent.querySelector('img');
        const caption = blockContent.querySelector('input');

        console.log("block data",blockContent)
    
        // return {
        //   url: image.src,
        //   caption: caption.value
        // }
    }

    validate(savedData){
        if (!savedData.url.trim()){
          return false;
        }
    
        return true;
      }

      _createImage(url){
        const image = document.createElement('img');
        image.setAttribute("style","width: 100%;")
        const caption = document.createElement('input');
    
        image.src = url;
        caption.placeholder = 'Caption...';
    
        this.wrapper.innerHTML = '';
        this.wrapper.appendChild(image);
        this.wrapper.appendChild(caption);
      }

  }

export default DashboardAddPostTest
