import React,{useContext,useEffect,useState} from "react"
import {
    CModal,
    CModalHeader,
    CButton,
    CModalBody,
    CModalFooter,
    CRow,
    CCol,
    CProgress
  } from '@coreui/react'
  import axios from "axios";
  import FormikLoginContainer from '../formik-wrappers/FormikLoginContainer'
  import AuthProvider from '../context/AuthContext'


  const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

  const PUBLIC_URL = "http://192.168.43.122:8080/laravel/site40/public";

  const Styles={
    link:{
      textDecoration:"underline",
      fontSize:"11px",
      color:"green"
    },
    Titles:{
      color:"black"
    },
    Theme:{
      background:"rgb(250, 252, 255)"
    }
  }
    


const LoginModal=({showToast})=>{

    const {modal,setModal}=useContext(AuthProvider.Context)

     //alert(modal)

    return (
        <>
      
      <CModal
      size="sm"
        show={true}
        onClose={()=>setModal(!modal)}
      >
        <CModalHeader closeButton><h1>Login</h1> </CModalHeader>
        <CModalBody className="text-left">
          <CRow>
              <CCol md="12" className="text-center">
           
             <FormikLoginContainer />
              </CCol>
          </CRow>
         
        </CModalBody>
        <CModalFooter>
        
          <CButton
            color="secondary"
            onClick={()=>setModal(!modal)}
          >Cancel</CButton>
        </CModalFooter>
      </CModal>
    </>
           
      )

}



export default LoginModal
