import React, { createContext, useState,useEffect} from "react";
import { useHistory,useLocation,useParams } from "react-router-dom";
import PropTypes from "prop-types";
import axios from "axios"

const Context = createContext({});

const initialArticle={
    id:null,
    title_en: "",
    body_en: null,
    featured_en:null,
    title_fr: "",
    body_fr: null,
    featured_fr:null,
    status:"draft",
    category:"",
    created_at:null,
    updated_at:null
}


const initialEditArticle={
 id:null,
 title_en: "",
 body_en: null,
 featured_en:null,
 title_fr: "",
 body_fr: null,
 featured_fr:null,
 status:"draft",
 category:"",
 created_at:null,
 updated_at:null
}


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

 const Provider = props => {
  // Initial values are obtained from the props
 const history = useHistory();

const {id} = useParams();

const location = useLocation();

let errorCount
 
  const {
    // article:initialArticle,
    children
  } = props;

  // Use State to keep the values
  const [article,setArticle] = useState(initialArticle);
  const [articles,setArticles] = useState([]);
  const [pagination,setPagination] = useState({});
  const [toast,setToast]=useState(false)
  const [toastType,setToastType]=useState("error")
  const [modal,setModal]=useState(false)
  const [errors,setErrors]=useState([])
  const [selectedImageEn,setSelectedImageEn]=useState("")
  const [selectedImageFr,setSelectedImageFr]=useState("")
  const [featuredFor,setFeaturedFor]=useState("")
  const [editMode,setEditMode]=useState("new")
  const [currentArticle,setCurrentArticle]=useState("")
  const [currentPage,setCurrentPage]=useState(1)
  const [searchPhrase,setSearchPhrase]=useState("")
  const [fetching,setFetching]=useState(false)
  const [fetchingFailMsg,setFetchingFailMsg]=useState(null)


  // const [featuredFor,setFeaturedFor]=useState("")

  useEffect(()=>{
//console.log("id",id)
updateFeatured()
console.log("articles rebuilt",articles[0])
console.log("pagination rebuilt",pagination)
console.log("article rebuilt",article)
  },[article,articles,pagination])
  

  const updateArticle=(dataKey,data)=> 
  {
     //console.log("data key", dataKey) 
    return setArticle(prevState => { 
         let newState=({...prevState,[dataKey]: data})
          return newState
        })  
  }

  const updateFeatured=()=> 
  {
     //console.log("data key", dataKey) 
     article.featured_en!==null && setSelectedImageEn( article.featured_en)

     article.featured_fr!==null && setSelectedImageFr( article.featured_fr)
    
  }
  
  const resetArticle=()=> 
  {
    setArticle(initialArticle)
    setSelectedImageEn("")
    setSelectedImageFr("")
    setCurrentArticle("")
    setToast(false)
  }



  const resetEditArticle=()=> 
  {
    setArticle(initialEditArticle)
  }


  const validateArticle= ()=>{
      setErrors([])
      setToast(false)
      errorCount=0
      let tmpErrors=[]
        if(article.status=="published"){
        if(article.title_en==""){
          tmpErrors.push("Please enter article title (english) before publishing as (published status)")
           errorCount++
        }

        if(article.body_en==null){
          tmpErrors.push("Please enter article body (english) before publishing as (published status)")
          errorCount++
        } 

        if(article.category==""){
          tmpErrors.push("Please select category before publishing as (published status)")
          errorCount++
      } 

       

        }

        if(article.status=="draft"){
            if(article.title_en==""){
              tmpErrors.push("Please enter article title (english) before publishing as (draft status)")
          errorCount++
               
            } 
            
           
        }

       
        errorCount>0 && setErrors(prevError=>[...prevError,...tmpErrors])

       editMode=="new" && errorCount==0 && publishArticleApi()

       editMode=="update" && errorCount==0 && updateArticleApi()
       
        

  }
  

  const publishArticleApi =()=>{

    axios.request( {
      method: "post", 
      url: API_URL+"post/new", 
      data: article
    }).then (response => {
      // updateGallery([])
      //   updateGallery(prevGallery=>[
      //     ...prevGallery,...response.data.data.data.data
      //   ])
      setEditMode("update")
      history.replace({
        pathname: '/posts/edit/'+response.data.data.data.id,
        //search: '?query=abc',
        id:response.data.data.data.id,
        state: { id: response.data.data.data.id,navType:"post_save",data:response.data.data.data }
    });
      console.log("post response",response.data.data.data.id)
    }).catch(error => {
      setErrors([])
      error.response.status==409 ? setErrors(prevError=>[...prevError,error.response.data.message]) :setErrors(prevError=>[...prevError,error.response.statusText])
      //console.log("error",error.response)
      
  });

  }



  
  const updateArticleApi =()=>{

    axios.request( {
      method: "post", 
      url: API_URL+"post/update", 
      data: article
    }).then (response => {
      // updateGallery([])
      //   updateGallery(prevGallery=>[
      //     ...prevGallery,...response.data.data.data.data
      //   ])
      setEditMode("update")
      setToastType("success")
      setErrors(prevError=>[...prevError,"Updated"])
      setToast(true)
      
     
      console.log("post response",response.data.data.data.id)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const fetchArticleByIdApi =(id)=>{

    axios.get( API_URL+"post/"+id).then (response => {
      setArticle(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
     
      console.log("post response",response.data.data.data)
      console.log("all articles",articles)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }



  const fetchArticlesApi =()=>{
    setArticles([])
    setFetching(true)
    setFetchingFailMsg(null)

    //alert("Hi there man")

    axios.get( API_URL+"posts").then (response => {

      console.log("fetch articles",response.data.data.data.data)
      setArticles(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
      setFetching(false)
      console.log("post response all articles",response.data.data.data)
    }).catch(error => {
      setErrors([])
      setFetching(false)
      error.response!==undefined ? setFetchingFailMsg("No articles found") : setFetchingFailMsg("Unknown error")
      // let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      // setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const goToPageApi =(page)=>{
    setArticles([])
    setCurrentPage(page)

    axios.get( pagination.path+"?page="+page).then (response => {
      setArticles(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
     
      console.log("post response all articles",response.data.data.data)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const searchArticleByPhraseApi =()=>{
    
    setArticles([])
    setFetching(true)
    setFetchingFailMsg(null)

    axios.get( API_URL+"post/search/"+searchPhrase).then (response => {

      
      setArticles(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
      setFetching(false)
      alert("we here")
      !articles.data.data.data.length && setFetchingFailMsg("No results found...")
      // alert(JSON.stringify(response.data) )
      console.log("get response",response.data.data.data)
      //console.log("all articles",articles)
    }).catch(error => {
      setErrors([])
      setFetching(false)
      //alert(JSON.stringify(error.response))
      //error.response!==undefined ? setFetchingFailMsg("No articles found") : setFetchingFailMsg("Unknown error")
      // let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      // setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const articleContext = {
    article,
    articles,
    updateArticle,
    toast,
    setToast,
    modal,
    setModal,
    errors,
    validateArticle,
    selectedImageFr,
    selectedImageEn,
    setSelectedImageEn,
    setSelectedImageFr,
    setFeaturedFor,
    featuredFor,
    editMode,
    setCurrentArticle,
    setArticle,
    setEditMode,
    resetArticle,
    resetEditArticle,
    initialEditArticle,
    toastType,
    setToastType,
    fetchArticleByIdApi,
    fetchArticlesApi,
    pagination,
    goToPageApi,
    currentPage,
    searchPhrase,
    setSearchPhrase,
    searchArticleByPhraseApi,
    setCurrentPage,
    fetching,
    fetchingFailMsg

    

  };

  // pass the value in provider and return
  return <Context.Provider value={articleContext}  {...props}>{children }</Context.Provider>;
};


const ArticleProvider= {
    Provider,
    Context
};

Provider.propTypes = {
    article:PropTypes.object,  
};

Provider.defaultProps = {
  article: {
    title_en: "",
    body_en: null,
    featured_en:null,
    title_fr: "",
    body_fr: null,
    featured_fr:null,
    status:"draft"
  },


};


export default ArticleProvider
