import React,{useContext} from 'react';
import { Editor } from '@tinymce/tinymce-react';
import ArticleContext from "../context/ArticleContext"

const RichText =({actionType})=> {
  const {currentArticle,editMode,article,updateArticle}=useContext(ArticleContext.Context)

 let key="xxx"



  const setUpdateModeOrNot=()=>{

    if(actionType=="en" && editMode=="update"){
      key=Math.random()
      console.log("Editor key",key)
      return  article.body_en

    }

    if(actionType=="fr" && editMode=="update"){
      key=Math.random()
      console.log("Editor key",key)
      return  article.body_fr

    }

    return "mmmmmmmmmmmmmmm"


  }


  const handleEditorChange = (content) => {
    // console.log(
    //   'Content was updated:',
    //   content
    // );

    // console.log(
    //   'Action Type:',
    //   actionType
    // );
  }

  
    return (
      <Editor
      key={key}
      apiKey="mymryl9rtn2npjz2vhp3yovdx2of4phahbqil1sq29upca83"
       content={editMode=="update" ? setUpdateModeOrNot():""}
        init={{
          height: 500,
          menubar: false,
          plugins: [
            'advlist autolink lists link image',
            'charmap print preview anchor help',
            'searchreplace visualblocks code',
            'insertdatetime media table paste wordcount'
          ],
          toolbar:
            'undo redo | formatselect | bold italic | \
            alignleft aligncenter alignright | \
            bullist numlist outdent indent | help'
        }}

        onEditorChange={(e)=>handleEditorChange(e)}

        
      />
    );

}

export default RichText;
