import React, { createContext, useState } from "react";
import PropTypes from "prop-types";

const PgCatContext = createContext({});

 const PgCatProvider = props => {
  // Initial values are obtained from the props
  const {
    categories: initialCategories,
    //selectedUser: initialSelectedUsers,
    children
  } = props;

  // Use State to keep the values
  const [categories, setCategories] = useState(initialCategories);
  //const [selectedUser, setSelectedUser] = useState(initialSelectedUsers);

  // Make the context object:
  const categoriesContext = {
    categories,
    setCategories,
  };

  // pass the value in provider and return
  return <PgCatContext.Provider  >{children}</PgCatContext.Provider>;
};

const PgCatProviderWrapper= {
    PgCatProvider,
    PgCatContext
};

PgCatProvider.propTypes = {
  categories: PropTypes.array,
  //selectedUser: PropTypes.object
};

PgCatProvider.defaultProps = {
  categories: ["Previous","ongoing","upcoming"],
  //selectedUser: {}
};


export default PgCatProviderWrapper
