import React,{useState,useEffect,useContext} from 'react'
import axios from "axios";
import ArticleContext from "../context/ArticleContext"


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

const PUBLIC_URL = "http://192.168.43.122:8080/laravel/site40/public/";

const ImageUpload= ({postType,target})=>{

  const {setModal}=useContext(ArticleContext.Context)

    //console.log("target",target)

    const triggerSelectInput=(e,input)=>{
        e.preventDefault()
        setModal(true)
    }

    useEffect(()=>{
        const uploadBt=document.getElementById(target)
        const uploadInputName="modal-upload-bt"
        //postType=="en" ? "upload_input_en" : "upload_input_fr"
        const uploadInput=document.getElementById(uploadInputName)
        uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,uploadInput))
    },[imagePreviewUrlEn,imagePreviewUrlEn])

    const [imagePreviewUrlEn,setImagePreviewUrlEn]=useState(null)
    const [imagePreviewUrlFr,setImagePreviewUrlFr]=useState(null)
    const [image,setImage]=useState("")

    console.log("French Banner",imagePreviewUrlFr)
    //console.log("English Banner",imagePreviewUrlEn)



    const onFormSubmit=(e)=>{
        //console.log(e.target.value)
    }

    return (<form onSubmit={()=>onFormSubmit}  encType="multipart/form-data" >
       
         {postType=="en"?
        <input id="upload_input_en" type="file" multiple={true} hidden={true} onChange={(e)=>onChange(e)} />:"" }
        <img className="img-fluid" src={imagePreviewUrlEn} />

        {postType=="fr"?
        <input id="upload_input_fr" type="file" hidden={true} onChange={(e)=>onChange(e)} />:"" }
        <img className="img-fluid" src={imagePreviewUrlFr} />


        </form>)
     
    
}

export default ImageUpload
