import React,{useContext,useEffect} from 'react'
import ArticleProvider from "../context/ArticleContext"
import {
    CInputGroup,
    CInputGroupPrepend,
    CInputGroupText,
    CInput,
  } from '@coreui/react'
  import CIcon from '@coreui/icons-react'

  const setUpdateModeOrNot=()=>{


  }





const ArticleStatus=()=>{
    const {article,updateArticle}=useContext(ArticleProvider.Context)

    useEffect(()=>{

       alert(article.status)

    },[article.status])


    return (
    <div className={""}>
        <label>Published &nbsp;</label>
       <input  type="radio"  id="statusp" name="status" defaultChecked={article.status=="published"?true:false} value="published" onClick={(e)=>{
         updateArticle("status", e.target.value)
         //console.log("article",article)
         }} />  


         <label>&nbsp;&nbsp;&nbsp;Draft &nbsp;</label>
         <input  type="radio"  id="statusd" name="status" value="draft" defaultChecked={article.status=="draft"?true:false} onClick={(e)=>{
         updateArticle("status", e.target.value)
         //console.log("article",article)
         }} />

     </div>
   

    )
    

   

}
 export default ArticleStatus
