import React,{useState,useEffect} from 'react'




const ImageUpload= ({postType,target})=>{

    //console.log("target",target)

    const triggerSelectInput=(e,input)=>{
        e.preventDefault()
        input.click()
    }

    useEffect(()=>{
        const uploadBt=document.getElementById(target)
        const uploadInputName=postType=="en" ? "upload_input_en" : "upload_input_fr"
        const uploadInput=document.getElementById(uploadInputName)

        uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,uploadInput))
        //console.log("use effect reloaded")
        //console.log("Post type",postType)
        console.log("French Banner",imagePreviewUrlFr)
        console.log("English Banner",imagePreviewUrlEn)
    },[imagePreviewUrlEn,imagePreviewUrlEn])

    const [imagePreviewUrlEn,setImagePreviewUrlEn]=useState(false)
    const [imagePreviewUrlFr,setImagePreviewUrlFr]=useState(false)
    const [image,setImage]=useState("")

    console.log("French Banner",imagePreviewUrlFr)
    //console.log("English Banner",imagePreviewUrlEn)

  

    const onChange=(e)=>{
        //console.log(e.target.value)
        let files = e.target.files || e.dataTransfer.files;
        //alert("on change for loading files")
        if (!files.length)
        return;
        //this.createImage(files[0]);
        let reader = new FileReader();
        reader.onload = (e) => {
        setImage(e.target.result)
        postType=="en" ? setImagePreviewUrlEn(e.target.result) : setImagePreviewUrlFr(e.target.result)
        //console.log("image",e.target.result)
        };
        reader.readAsDataURL(files[0]);
    }

    const onFormSubmit=(e)=>{
        //console.log(e.target.value)
    }

    return (<form onSubmit={()=>onFormSubmit}  encType="multipart/form-data" >
       
         {postType=="en"?
        <input id="upload_input_en" type="file" hidden="true" onChange={(e)=>onChange(e)} />:"" }
        <img className="img-fluid" src={imagePreviewUrlEn} />

        {postType=="fr"?
        <input id="upload_input_fr" type="file" hidden="true" onChange={(e)=>onChange(e)} />:"" }
        <img className="img-fluid" src={imagePreviewUrlFr} />


        {/* <div className="imgPreview">
        {imagePreviewUrlEn && postType=="en" ? (<img className="add_imagem img-fluid" Name="add_imagem" src={
            imagePreviewUrlEn} />) : ( '' )
        }

        { imagePreviewUrlFr && postType=="fr"? (<img className="add_imagem img-fluid" Name="add_imagem" src={imagePreviewUrlFr} />) : ( '' )
        }
        </div> */}


        </form>)
     
    
}

export default ImageUpload
