import React,{useState,useEffect} from 'react'


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

const ImageUpload= ({postType,target})=>{

    //console.log("target",target)

    const triggerSelectInput=(e,input)=>{
        e.preventDefault()
        input.click()
    }

    useEffect(()=>{
        const uploadBt=document.getElementById(target)
        const uploadInputName=postType=="en" ? "upload_input_en" : "upload_input_fr"
        const uploadInput=document.getElementById(uploadInputName)
        uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,uploadInput))
    },[imagePreviewUrlEn,imagePreviewUrlEn])

    const [imagePreviewUrlEn,setImagePreviewUrlEn]=useState(null)
    const [imagePreviewUrlFr,setImagePreviewUrlFr]=useState(null)
    const [image,setImage]=useState("")

    console.log("French Banner",imagePreviewUrlFr)
    //console.log("English Banner",imagePreviewUrlEn)

  

    const onChange=(e)=>{
        //console.log(e.target.value)
        let file = e.target.files[0] || e.dataTransfer.files[0];
        let allowed_mime_types = [ 'image/jpeg', 'image/png' ];
        let allowed_size_mb = 2;
        
        if(allowed_mime_types.indexOf(file.type) == -1) {
			alert('Error : Incorrect file type');
			return;
		}

		if(file.size > allowed_size_mb*1024*1024) {
			alert('Error : Exceeded size');
			return;
		}
        //alert("on change for loading files")
        if (!file)
        return;
        //this.createImage(files[0]);
        let reader = new FileReader();
        reader.onload = (e) => {
        setImage(reader.result)
        //postType=="en" ? setImagePreviewUrlEn(reader.result) : setImagePreviewUrlFr(reader.result)
        //console.log("image",e.target.result)
        };
        reader.readAsDataURL(file);

        let data = new FormData();
        const uploadInputName=postType=="en" ? "upload_input_en" : "upload_input_fr"
        const uploadInput=document.getElementById(uploadInputName)
		data.append('file', document.getElementById(uploadInputName).files[0]);

		let request = new XMLHttpRequest();
		request.open('POST', API_URL+'photo/upload'); 
		request.addEventListener('load', function(e) {
			console.log(request.response);
		});
		request.send(data);
    }

    const onFormSubmit=(e)=>{
        //console.log(e.target.value)
    }

    return (<form onSubmit={()=>onFormSubmit}  encType="multipart/form-data" >
       
         {postType=="en"?
        <input id="upload_input_en" type="file" hidden={true} onChange={(e)=>onChange(e)} />:"" }
        <img className="img-fluid" src={imagePreviewUrlEn} />

        {postType=="fr"?
        <input id="upload_input_fr" type="file" hidden={true} onChange={(e)=>onChange(e)} />:"" }
        <img className="img-fluid" src={imagePreviewUrlFr} />



        </form>)
     
    
}

export default ImageUpload
