import React,{useContext} from "react"
import {
    CModal,
    CModalHeader,
    CButton,
    CModalBody,
    CModalFooter,
    CRow,
    CCol
  } from '@coreui/react'
  import ArticleProvider from "../context/ArticleContext"



const ModalMe=({showToast})=>{


  useEffect(()=>{
    const uploadBt=document.getElementById("modal-upload-bt")
    const uploadInputName="modal-upload-input"
    //postType=="en" ? "upload_input_en" : "upload_input_fr"
    const uploadInput=document.getElementById(uploadInputName)
    uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,uploadInput))
},[imagePreviewUrlEn,imagePreviewUrlEn])


    const {modal,setModal}=useContext(ArticleProvider.Context)
    return (
        <>
      
      <CModal
      size="lg"
        show={modal}
        onClose={()=>setModal(!modal)}
      >
        <CModalHeader closeButton>Gallery</CModalHeader>
        <CModalBody>
          <CRow>
              <CCol md="8">Left</CCol>
              <CCol md="4">Right</CCol>
          </CRow>
          <CRow className="d-flex flex-row justify-content-center">
              {/* <CCol md="8">Left</CCol>
              <CCol md="4">Right</CCol> */}
              <img className="img-fluid" src="" />
              <CCol md="4">
              {/* <input id="upload_input_en" type="file"  hidden={true} onChange={(e)=>{}} /> */}
              <CButton id="modal-upload-bt" color="primary" size="md" block md="12">Add IMage</CButton>
              </CCol>
          </CRow>
        </CModalBody>
        <CModalFooter>
          <CButton color="primary">Do Something</CButton>{' '}
          <CButton
            color="secondary"
            onClick={()=>setModal(!modal)}
          >Cancel</CButton>
        </CModalFooter>
      </CModal>
    </>
           
      )

}



export default ModalMe
