import React,{useState} from 'react'

const ImageUpload= ()=>{

    const [imagePreviewUrl,setImagePreviewUrl]=useState(false)
    const [image,setImage]=useState("")

    const onChange=(e)=>{
        //console.log(e.target.value)
        let files = e.target.files || e.dataTransfer.files;
        console.log("on change for loading files")
        if (!files.length)
        return;
        //this.createImage(files[0]);
        let reader = new FileReader();
        reader.onload = (e) => {
        setImage(this.result)
        setImagePreviewUrl(this.result)
        console.log("image",e.target.result)
        };
        reader.readAsDataURL(files[0]);
    }

    const onFormSubmit=(e)=>{
        console.log(e.target.value)
    }

    return (<form onSubmit={()=>onFormSubmit}  encType="multipart/form-data" >
       
        <input className="input_imagem_artigo" type="file"  onChange={(e)=>onChange()} />
        <div className="imgPreview">
        { imagePreviewUrl ?  (<img className="add_imagem" Name="add_imagem" src={imagePreviewUrl} />) : ( 'Upload image' )
        }
        </div>
        </form>)
     
    
}

export default ImageUpload
