import React, { useState, useEffect,useContext } from 'react'
import { useHistory, useLocation } from 'react-router-dom'
import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow,
  CFormGroup,
  CInput,
  CLabel,
  CPagination,
  CButton
} from '@coreui/react'
import UserProvider from "../../context/UserContext"
import UserRole from "../../inputs/user-role"
import { useFormik } from 'formik';
import * as Yup from 'yup';
import usersData from '../../views/users/UsersData'
import ToastMe from "../../alerts/toaster"

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const DashboardAddUser = () => {


    const {fetchUsersApi,users,setUser,addUserApi}=useContext(UserProvider.Context)

    const Styles={
        errorColor:{
            color:"red"
        }
    }

    const formik = useFormik({
        initialValues: {
          name: '',
          email: '',
          role: 'editor',
        },
        validationSchema: Yup.object({
          name: Yup.string()
            .max(15, 'Must be 7 characters or less')
            .required('Name field required'),
          email: Yup.string().email('Invalid email address').required('Required'),
          role: Yup.string().required('Email address required'),
        }),
        onSubmit: values => {
            setUser(values)
            addUserApi()
          alert(JSON.stringify(values, null, 2));
        },
      });

  useEffect(() => {
    // currentPage !== page && setPage(currentPage)
  }, [])

  return (
      
    <CRow>
      <CCol xl={6}>
        <CCard>
          <CCardHeader>
          Add User
          </CCardHeader>
          <CCardBody>
          <form onSubmit={formik.handleSubmit}>
          <CRow>
                <CCol xs="12">
                  <CFormGroup>
                    <CLabel htmlFor="name">Name</CLabel>
                    <CInput 
                    id="name" 
                    placeholder="Enter your name" 
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.name}
                     />
                  </CFormGroup>
                  {formik.touched.name && formik.errors.name ? (
         <><div style={Styles.errorColor}>{formik.errors.name}</div><br /></>
       ) : null}
                </CCol>
              </CRow>
              <CRow>
                <CCol xs="12">
                  <CFormGroup>
                    <CLabel htmlFor="ccnumber">Email</CLabel>
                    <CInput id="email"
                     type="email"
                      placeholder="Enter email here.." 
                      placeholder="Enter your name" 
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.email}
                      
                       />
                  </CFormGroup>
                  {formik.touched.email && formik.errors.email ? (
         <div style={Styles.errorColor}>{formik.errors.email}</div>
       ) : null}
                </CCol>
              </CRow>

              <CRow className="text-center">
              <CCol xs="12">
                   <UserRole formik={formik} />
                  </CCol>
                  </CRow>

              
              <CRow className="text-center">
              <CCol xs="12">
                  <CButton type="submit" color="primary" size="md" block md="10">Add</CButton>
                  </CCol>
              </CRow>
         </form>
          </CCardBody>
        </CCard>
      </CCol>


      <CCol xl={6}>
        <CCard>
          <CCardHeader>
            Users
            <small className="text-muted"> example</small>
          </CCardHeader>
          <CCardBody>
          <CDataTable
            items={usersData}
            fields={[
              { key: 'name', _classes: 'font-weight-bold' },
              'registered', 'role', 'status'
            ]}
            hover
            striped
            itemsPerPage={5}
            clickableRows
            onRowClick={(item) => {}}
            scopedSlots = {{
              'status':
                (item)=>(
                  <td>
                    <CBadge color={getBadge(item.status)}>
                      {item.status}
                    </CBadge>
                  </td>
                )
            }}
          />
         
          </CCardBody>
        </CCard>
      </CCol>
      <ToastMe showToast={toast} />
    </CRow>
  )
}

export default DashboardAddUser
