import React, { useState, useEffect,useContext } from 'react'
import { useHistory, useLocation } from 'react-router-dom'
import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow,
  CFormGroup,
  CInput,
  CLabel,
  CPagination,
  CButton
} from '@coreui/react'
import UserProvider from "../../context/UserContext"
import UserRole from "../../inputs/user-role"
import { useFormik } from 'formik';
import * as Yup from 'yup';
import usersData from '../../views/users/UsersData'
import Paginations from "../../pagination/pagination"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash} from "@fortawesome/free-solid-svg-icons";
import LoginModal from '../../modals/login-modal'


const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const DashboardUserSetting= () => {


    const {toast,fetchUsersApi,users,setUser,addUserApi,errors,deleteUserApi,apiAction,fetchingFailMsg}=useContext(UserProvider.Context)

    useEffect(()=>{
    
  fetchUsersApi()

    },[])

    const Styles={
        errorColor:{
            color:"red"
        }
    }

    const formik = useFormik({
        initialValues: {
          name: '',
          email: '',
          password:'',
          role: 'editor',
        },
        validationSchema: Yup.object({
          password: Yup.string().required('Password is required'),
          passwordConfirmation: Yup.string()
             .oneOf([Yup.ref('password'), null], 'Passwords must match')
        }),
        onSubmit: values => {
            //setUser(values)
            addUserApi(values)
          //alert(JSON.stringify(values, null, 2));
        },
      });

  useEffect(() => {
    // currentPage !== page && setPage(currentPage)
  }, [])

  return (
      
    <CRow>
      <CCol xl={5}>
        <CCard>
          <CCardHeader>
          Update Password
          </CCardHeader>
          <CCardBody>
          <form onSubmit={formik.handleSubmit}>
          <CRow>
                <CCol xs="12">
                  <CFormGroup>
                    <CLabel htmlFor="name">New Password</CLabel>
                    <CInput 
                    id="name" 
                    placeholder="Enter your new password" 
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.name}
                     />
                  </CFormGroup>
                  {formik.touched.name && formik.errors.name ? (
         <><div style={Styles.errorColor}>{formik.errors.name}</div><br /></>
       ) : null}
                </CCol>
              </CRow>
              <CRow>
                <CCol xs="12">
                  <CFormGroup>
                    <CLabel htmlFor="ccnumber">Confirm New Password</CLabel>
                    <CInput id="email"
                     type="text"
                      placeholder="Confirm password here.." 
                      placeholder="Enter your name" 
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.email}
                      
                       />
                  </CFormGroup>
                  {formik.touched.email && formik.errors.email ? (
         <div style={Styles.errorColor}>{formik.errors.email}</div>
       ) : null}
                </CCol>
              </CRow>

              

              
              <CRow className="text-center">
              <CCol xs="12">
              {apiAction ? 
                    <button className="btn btn-primary px-4 w-100"   disabled={apiAction}>
                      <span
                        className="spinner-grow spinner-grow-sm"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      &nbsp;Loading...
                    </button> : 
                    <button className="btn btn-primary px-4 w-100"  disabled={apiAction}>
                    
                      Add
                    </button>}
                  <div style={{color:"red"}} className="mt-2">
                  {errors.length ? <h3>Submission contains errors</h3>:""}
                      {errors.length ?errors.map( (error,i)=><p key={i} className="text-left">{error}</p>):""}
                      </div>
                  </CCol>
              </CRow>
         </form>
          </CCardBody>
        </CCard>
      </CCol>


      
      <LoginModal context={UserProvider.Context}/>
      
    </CRow>
  )
}

export default DashboardUserSetting
