import React from "react"
import {
    CToaster,
    CToast,
    CToastHeader,
    CToastBody,
  } from '@coreui/react'



const ToastMe=({show})=>{
    return (
        <>
            <CToaster>
            
                  <CToast
                    style={{background:"pink",color:"wine"}}
                    show={show}
                    autohide={"3000"}
                    fade={true}
                    className="success"
                  >
                    <CToastHeader >
                      Toast title
                    </CToastHeader>
                    <CToastBody>
                      This is a toast
                    </CToastBody>
                  </CToast>
            </CToaster>
         
        </>
      )

}



export default ToastMe
