import React, { useState,useContext,useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CPagination
} from '@coreui/react'
import ArticleProvider from "../context/ArticleContext"

const Paginations = () => {
 
  const {currentPage,goToPageApi,pagination}=useContext(ArticleProvider.Context)


let pagesToRender=Math.round(pagination.total/pagination.per_page)

  useEffect(()=>{

console.log("pagination from paginate",pagesToRender)
  },[])

  return (
    <>      
          <CPagination
            align="center"
            size="lg"
            addListClass="some-class"
            activePage={currentPage}
            pages={pagesToRender}
            onActivePageChange={(page)=>goToPageApi(page)}
          />
          <br></br>

         
    </>
  )
}

export default Paginations
