import React,{useContext} from "react"
import {
    CModal,
    CModalHeader,
    CButton,
    CModalBody,
    CModalFooter
  } from '@coreui/react'
  import ArticleProvider from "../context/ArticleContext"



const ModalMe=({showToast})=>{
    const {modal,setModal}=useContext(ArticleProvider.Context)
    return (
        <>
      <CButton
        onClick={()=>setModal(false)}
        className="mr-1"
      >Launch demo modal</CButton>
      <CModal
        show={modal}
        onClose={modal}
      >
        <CModalHeader closeButton>Modal title</CModalHeader>
        <CModalBody>
          Lorem ipsum dolor...
        </CModalBody>
        <CModalFooter>
          <CButton color="primary">Do Something</CButton>{' '}
          <CButton
            color="secondary"
            onClick={setModal(false)}
          >Cancel</CButton>
        </CModalFooter>
      </CModal>
    </>
           
      )

}



export default ModalMe
