import React,{useState,useEffect,useContext} from 'react'
import axios from "axios";
import ArticleContext from "../context/ArticleContext"


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

const PUBLIC_URL = "http://192.168.43.122:8080/laravel/site40/public/";

const ImageUpload= ({postType,target})=>{

  const {setModal,selectedImageEn,selectedImageFr}=useContext(ArticleContext.Context)

    //console.log("target",target)

    const triggerSelectInput=(e,input)=>{
        e.preventDefault()
        setModal(true)
    }

    useEffect(()=>{
        const uploadBt=document.getElementById(target)
        console.log("target",target)
        const uploadInputName="modal-upload-bt"
        //postType=="en" ? "upload_input_en" : "upload_input_fr"
        const uploadInput=document.getElementById(uploadInputName)
        uploadBt.map(el=>console.log("element",el))
        
        uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,uploadInput))
    },[])

    

    return postType=="en" ? <img className="img-fluid" src={selectedImageEn} /> : <img className="img-fluid" src={selectedImageEn} />
        
    
}

export default ImageUpload
