import React, { createContext, useState,useEffect} from "react";
import { useHistory,useLocation,useParams } from "react-router-dom";
import PropTypes from "prop-types";
import axios from "axios"

const Context = createContext({});

const initialArticle={
    title_en: "",
    body_en: null,
    featured_en:null,
    title_fr: "",
    body_fr: null,
    featured_fr:null,
    status:"draft",
    category:""
}


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

 const Provider = props => {
  // Initial values are obtained from the props
 const history = useHistory();

const {id} = useParams();

const location = useLocation();
 
  const {
    // article:initialArticle,
    children
  } = props;

  // Use State to keep the values
  const [article,setArticle] = useState(initialArticle);
  const [toast,setToast]=useState(false)
  const [modal,setModal]=useState(false)
  const [errors,setErrors]=useState([])
  const [selectedImageEn,setSelectedImageEn]=useState("")
  const [selectedImageFr,setSelectedImageFr]=useState("")
  const [featuredFor,setFeaturedFor]=useState("")
  const [editMode,setEditMode]=useState("new")
  // const [featuredFor,setFeaturedFor]=useState("")

  useEffect(()=>{
console.log("id",id)
  },[article])
  
  const updateArticle=(dataKey,data)=> 
  {
     //console.log("data key", dataKey) 
    return setArticle(prevState => { 
         let newState=({...prevState,[dataKey]: data})
          return newState
        })  
  }

  
  const validateArticle= ()=>{
      setErrors([])
      setToast(false)
        if(article.status==="published"){
        if(article.title_en===""){
            setErrors(prevError=>[...prevError,"Please enter article title (english) before publishing as (published status)"])
        } 
        if(article.body_en===null){
            setErrors(prevError=>[...prevError,"Please enter article body (english) before publishing as (published status)"])
        } 

        if(article.category===""){
          setErrors(prevError=>[...prevError,"Please select category before publishing as (published status)"])
      } 

        console.log("Errors",console.errors)

        }

        if(article.status==="draft"){
            if(article.title_en===""){
                setErrors(prevError=>[...prevError,"Please enter article title (english) before publishing as (draft status)"])
            } 
            

        }

        !errors.length && publishArticle() 

        

  }
  

  const publishArticle =()=>{

    axios.request( {
      method: "post", 
      url: API_URL+"post/new", 
      data: article
    }).then (response => {
      // updateGallery([])
      //   updateGallery(prevGallery=>[
      //     ...prevGallery,...response.data.data.data.data
      //   ])
      setEditMode("update")
      history.push({
        pathname: '/posts/edit/'+response.data.data.data.id,
        //search: '?query=abc',
        id:response.data.data.data.id,
        state: { id: response.data.data.data.id,navType:"post_save",data:response.data.data.data }
    });
      console.log("post response",response.data.data.data.id)
    }).catch(error => {
      setErrors([])
      setErrors(prevError=>[...prevError,error.response.statusText])
      
  });

  }


  const articleContext = {
    article,
    updateArticle,
    toast,
    setToast,
    modal,
    setModal,
    errors,
    validateArticle,
    selectedImageFr,
    selectedImageEn,
    setSelectedImageEn,
    setSelectedImageFr,
    setFeaturedFor,
    featuredFor,
    editMode
  };

  // pass the value in provider and return
  return <Context.Provider value={articleContext}  {...props}>{children }</Context.Provider>;
};


const ArticleProvider= {
    Provider,
    Context
};

Provider.propTypes = {
    article:PropTypes.object,  
};

Provider.defaultProps = {
  article: {
    title_en: "",
    body_en: null,
    featured_en:null,
    title_fr: "",
    body_fr: null,
    featured_fr:null,
    status:"draft"
  },


};


export default ArticleProvider
