import React,{useState,useEffect} from 'react'




const ImageUpload= ({postType,target})=>{

    

    const triggerSelectInput=(e,input)=>{
        e.preventDefault()
        input.click()
    }

    useEffect((target)=>{
        const uploadBt=document.getElementById(target)
        const upload_input=document.getElementById("upload_input")

        uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,upload_input))
        console.log("use effect reloaded")
    },[])


    console.log("Post type",postType)

    const [imagePreviewUrl,setImagePreviewUrl]=useState(false)
    const [image,setImage]=useState("")

    const onChange=(e)=>{
        //console.log(e.target.value)
        let files = e.target.files || e.dataTransfer.files;
        //alert("on change for loading files")
        if (!files.length)
        return;
        //this.createImage(files[0]);
        let reader = new FileReader();
        reader.onload = (e) => {
        setImage(e.target.result)
        setImagePreviewUrl(e.target.result)
        console.log("image",e.target.result)
        };
        reader.readAsDataURL(files[0]);
    }

    const onFormSubmit=(e)=>{
        //console.log(e.target.value)
    }

    return (<form onSubmit={()=>onFormSubmit}  encType="multipart/form-data" >
       
         {!imagePreviewUrl ?
        <input id="upload_input" type="file" hidden="true" onChange={(e)=>onChange(e)} />:"" }


        <div className="imgPreview">
        { imagePreviewUrl ?  (<img className="add_imagem img-fluid" Name="add_imagem" src={imagePreviewUrl} />) : ( 'Upload image' )
        }
        </div>
        </form>)
     
    
}

export default ImageUpload
