import React, { createContext, useState } from "react";
import PropTypes from "prop-types";

const Context = createContext({});

 const Provider = props => {
  // Initial values are obtained from the props
  const {
//selectedUser: initialSelectedUsers,
    children
  } = props;

  // Use State to keep the values

  const initialArticle={
        titleEn: "",
        bodyEn: null,
        featuredEn:null,
        titleFr: "",
        bodyFr: null,
        featuredFr:null
    
  }

  const [article, setArticle] = useState(initialArticle);
  const [titleEn, setTitleEn] = useState("");
  const [bodyEn, setBodyEn] = useState(null);
  const [featuredEn, setFeaturedEn] = useState(null);

  const [titleFr, setTitleFr] = useState("");
  const [bodyFr, setBodyFr] = useState(null);
  const [featuredFr, setFeaturedFr] = useState(null);


  const updateArticle=(dataKey,data)=>{

      switch(dataKey){
              case 'titleEn':
              setArticle(prevState=>{ 
                  console.log(article);  
                  return {...prevState,data} 
                 })
              break;
              case 'bodyEn':
                  setBodyEn(data)
              break;
              case 'featuredEn':
                  setFeaturedEn(data)
              break;
              case 'titleFr':
              setTitleFr(data)
              break;
              case 'bodyFr':
                  setBodyFr(data)
              break;
              case 'featuredFr':
                  setFeaturedFr(data)
                  default:
                      return "action mismatch"


      }
   
  }




//const [selectedUser, setSelectedUser] = useState(initialSelectedUsers);

  // Make the context object:
  const articlesContext = {
    article,
    updateArticle,
  };

  // pass the value in provider and return
  return <Context.Provider value={articlesContext}>{children}</Context.Provider>;
};

const ArticleProvider= {
    Provider,
    Context
};

Provider.propTypes = {
    article:PropTypes.object,
    // article:PropTypes.shape(
    // {
    // titleEn: PropTypes.string,
    // bodyEn: PropTypes.string,
    // featuredEn:PropTypes.string,
    // titleFr: PropTypes.string,
    // bodyFr: PropTypes.string,
    // featuredFr:PropTypes.string
    // }
    // )
    
  
   
};

Provider.defaultProps = {
  article: {},
//   titleEn: "",
//   bodyEn:  "",
//   featuredEn: "",
//   titleFr: "",
//   bodyFr: "",
//   featuredFr:"",
//   //selectedUser: {}
};


export default ArticleProvider
