import React,{useContext} from "react"
import {
    CToaster,
    CToast,
    CToastHeader,
    CToastBody,
  } from '@coreui/react'
  import ArticleProvider from "../context/ArticleContext"
  import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';

 const notify = () => toast("Wow so easy !");

   

const ToastMe=({showToast})=>{
    const {toast,toastType,errors,setToast}=useContext(ArticleProvider.Context)

   
    return (
        <>
            {/* <CToaster>
            
                  <CToast
                    style={{background:toastType=="success" ? "green": "pink",color:toastType=="success" ? "white": "wine"}}
                    show={showToast}
                    autohide={5000}
                    fade={true}
                    className="success"
                  >
                    <CToastHeader >
                      {toastType=="success" ? "Success": "Error"}
                    </CToastHeader>
                    <CToastBody>
                        <ul>
                    {
                       errors.length? errors.map((error,i)=> {
                           setToast(true)
                           return (<li key={i}>{error}</li>)
                           }
                           
                           ) :""
                        //errors.length? setToast(true):""  
                           
                    }
                      </ul>
                    </CToastBody>
                  </CToast>
            </CToaster> */}

            
      <div>
        <button onClick={notify}>Notify !</button>
        <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        />
{/* Same as */}
<ToastContainer />
      </div>
    
         
        </>
      )

}



export default ToastMe
