import React, { createContext, useState,useEffect} from "react";
import { useHistory,useLocation,useParams } from "react-router-dom";
import PropTypes from "prop-types";
import axios from "axios"

const Context = createContext({});

const initialArticle={
    id:null,
    title_en: "",
    body_en: null,
    featured_en:null,
    title_fr: "",
    body_fr: null,
    featured_fr:null,
    status:"draft",
    category:"",
    created_at:null,
    updated_at:null
}


const initialEditArticle={
 id:null,
 title_en: "",
 body_en: null,
 featured_en:null,
 title_fr: "",
 body_fr: null,
 featured_fr:null,
 status:"draft",
 category:"",
 created_at:null,
 updated_at:null
}


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

 const Provider = props => {
  // Initial values are obtained from the props

let errorCount
 
  const {
    // article:initialArticle,
    children
  } = props;

  // Use State to keep the values
  const [attendees,setAttendees] = useState([]);
  const [pagination,setPagination] = useState({});
  const [currentPage,setCurrentPage]=useState(1)
  const [searchPhrase,setSearchPhrase]=useState("")
  const [errors,setErrors]=useState([])

  // const [featuredFor,setFeaturedFor]=useState("")
  useEffect(()=>{

  },[])
  




  const fetchAttendeesApi =()=>{
    setAttendees([])

    //alert("Hi there man")

    axios.get( API_URL+"programmes/attendees").then (response => {

      console.log("fetch articles",response.data.data.data.data)
      setAttendees(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
     
      console.log("post response all articles",response.data.data.data)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const goToPageApi =(page)=>{
    setAttendees([])
    setCurrentPage(page)

    axios.get( pagination.path+"?page="+page).then (response => {
      setAttendees(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
     
      console.log("post response all articles",response.data.data.data)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const searchAttendeesByPhraseApi =()=>{
    
    setAttendees([])

    axios.get( API_URL+"programmes/attendees/search/"+searchPhrase).then (response => {

      
      setAttendees(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
     
      console.log("get response",response.data.data.data)
      //console.log("all articles",articles)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const articleContext = {
    attendees,
    fetchAttendeesApi,
    pagination,
    goToPageApi,
    currentPage,
    searchPhrase,
    setSearchPhrase,
    searchAttendeesByPhraseApi
 

    

  };

  // pass the value in provider and return
  return <Context.Provider value={articleContext}  {...props}>{children }</Context.Provider>;
};


const AttendeeProvider= {
    Provider,
    Context
};

// Provider.propTypes = {
//     article:PropTypes.object,  
// };

// Provider.defaultProps = {
//   article: {
//     title_en: "",
//     body_en: null,
//     featured_en:null,
//     title_fr: "",
//     body_fr: null,
//     featured_fr:null,
//     status:"draft"
//   },


// };


export default AttendeeProvider
