import React, { useState,useContext } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CPagination
} from '@coreui/react'
import ArticleProvider from "../context/ArticleContext"

const Paginations = () => {
  const [currentPage, setCurrentPage] = useState(2)
  const {articles,fetchArticlesApi,pagination,fetchArticleByIdApi,resetEditArticle,resetArticle,setEditMode,setArticle,setCurrentArticle,editMode,selectedImageEn,selectedImageFr,validateArticle,toast,modal,setModal,setToast,article,updateArticle}=useContext(ArticleProvider.Context)

  return (
    <>
      

          
          <CPagination
            align="center"
            addListClass="some-class"
            activePage={currentPage}
            pages={Math.round(pagination.total/pagination.per_page)}
            onActivePageChange={()=>alert(currentPage)}
          />
          <br></br>

         
    </>
  )
}

export default Paginations
