import React, { createContext, useState,useEffect } from "react";
import PropTypes from "prop-types";

const Context = createContext({});

const initialArticle={
    titleEn: "",
    bodyEn: null,
    featuredEn:null,
    titleFr: "",
    bodyFr: null,
    featuredFr:null,
    status:"draft"

}



 const Provider = props => {
  // Initial values are obtained from the props
  const {
    article:initialArticle,
    children
  } = props;

  // Use State to keep the values



  const [article, setArticle] = useState(initialArticle);
  const [showToast,setShowToast]=useState(false)
  useEffect(()=>{console.log("article ii",article)},[article])

  const updateArticle=(dataKey,data)=> 
  {
     //console.log("data key", dataKey) 
    return setArticle(prevState => { 
         let newState=({...prevState,[dataKey]: data})
          return newState
        })  
  }
  


  const articleContext = {
    article,
    updateArticle,
  };

  // pass the value in provider and return
  return <Context.Provider value={articleContext}>{children}</Context.Provider>;
};


const ArticleProvider= {
    Provider,
    Context
};

Provider.propTypes = {
    article:PropTypes.object,  
};

Provider.defaultProps = {
  article: {
    titleEn: "",
    bodyEn: null,
    featuredEn:null,
    titleFr: "",
    bodyFr: null,
    featuredFr:null,
    status:"draft"
  },


};


export default ArticleProvider
