import React, { createContext, useState } from "react";
import PropTypes from "prop-types";

const ArticleContext = createContext({});

 const ArticleProvider = props => {
  // Initial values are obtained from the props
  const {
    articles: initialArticles,
    titleEn: initialTitleEn,
    bodyEn: initialBodyEn,
    featuredEn:initialFeaturedEn,
    titleFr: initialTitleFr,
    bodyFr: initialBodyFr,
    featuredFr:initialFeaturedFr,
//selectedUser: initialSelectedUsers,
    children
  } = props;

  // Use State to keep the values
  const [articles, setArticles] = useState(initialArticles);
  const [titleEn, setTitleEn] = useState(initialTitleEn);
  const [bodyEn, setBodyEn] = useState(initialBodyEn);
  const [featuredEn, setFeaturedEn] = useState(initialFeaturedEn);

  const [titleFr, setTitleFr] = useState(initialTitleFr);
  const [bodyFr, setBodyFr] = useState(initialBodyFr);
  const [featuredFr, setFeaturedFr] = useState(initialFeaturedFr);


  const updateArticle=(dataKey,data)=>{

      switch(dataKey){
              case 'titleEn':
              setTitleEn(data)
              break;
              case 'bodyEn':
                  setBodyEn(data)
              break;
              case 'featuredEn':
                  setFeaturedEn(data)
              break;
              case 'titleFr':
              setTitleFr(data)
              break;
              case 'bodyFr':
                  setBodyFr(data)
              break;
              case 'featuredFr':
                  setFeaturedFr(data)
                  default:
                      return "action mismatch"


      }
   
  }




//const [selectedUser, setSelectedUser] = useState(initialSelectedUsers);

  // Make the context object:
  const articlesContext = {
    articles,
    updateArticle,
  };

  // pass the value in provider and return
  return <ArticleContext.Provider value={articlesContext}>{children}</ArticleContext.Provider>;
};

const ArticleProviderWrapper= {
    ArticleProvider,
    ArticleContext
};

ArticleProvider.propTypes = {
    articles: PropTypes.array,
    titleEn: propTypes.string,
    bodyEn: propTypes.string,
    featuredEn:propTypes.string,
    titleFr: propTypes.string,
    bodyFr: propTypes.string,
    featuredFr:propTypes.string,
  
   
};

ArticleProvider.defaultProps = {
  articles: [],
  titleEn: "",
  bodyEn:  "",
  featuredEn: "",
  titleFr: "",
  bodyFr: "",
  featuredFr:"",
  //selectedUser: {}
};


export default ArticleProviderWrapper
