import React,{useContext} from 'react'
import ArticleProvider from "../context/ArticleContext"
import {
    CInputGroup,
    CInputGroupPrepend,
    CInputGroupText,
    CInput,
  } from '@coreui/react'
  import CIcon from '@coreui/icons-react'


const ArticleStatus=()=>{
    const {article,updateArticle}=useContext(ArticleProvider.Context)
    return (
    <div className={""}>
        <label>Published &nbsp;</label>
       <input  type="radio"  id="statusp" name="status" value="published" onClick={(e)=>{
         updateArticle("status", e.target.value)
         console.log("article",article)
         }} />  
         <label>&nbsp;&nbsp;&nbsp;Draft &nbsp;</label>
         <input  type="radio"  id="statusd" name="status" value="draft" defaultChecked onClick={(e)=>{
         updateArticle("status", e.target.value)
         console.log("article",article)
         }} />
     </div>
   

    )
    

   

}
 export default ArticleStatus
