import React, { createContext, useState,useEffect} from "react";
import { useHistory,useLocation,useParams } from "react-router-dom";
import PropTypes from "prop-types";
import axios from "axios"
import { useAlert } from 'react-alert'

const Context = createContext({});

const initialArticle={
    id:null,
    title_en: "",
    body_en: null,
    featured_en:null,
    title_fr: "",
    body_fr: null,
    featured_fr:null,
    status:"draft",
    category:"",
    created_at:null,
    updated_at:null
}


const initialEditArticle={
 id:null,
 title_en: "",
 body_en: null,
 featured_en:null,
 title_fr: "",
 body_fr: null,
 featured_fr:null,
 status:"draft",
 category:"",
 created_at:null,
 updated_at:null
}


const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

 const Provider = props => {
  // Initial values are obtained from the props

  const history=useHistory()

  const alert = useAlert()


const initialUser={
   username:"",
   email:"",
   role:"editor"
}
 
  const {
    // article:initialArticle,
    childrens
  } = props;

  // Use State to keep the values
  const [user,setUser] = useState(initialUser);
  const [users,setUsers] = useState([]);
  const [pagination,setPagination] = useState({});
  const [currentPage,setCurrentPage]=useState(1)
  const [searchPhrase,setSearchPhrase]=useState("")
  const [errors,setErrors]=useState([])
  const [fetching,setFetching]=useState(false)
  const [fetchingFailMsg,setFetchingFailMsg]=useState(null)
  const [toast,setToast]=useState(false)
  const [toastType,setToastType]=useState("error")

  // const [featuredFor,setFeaturedFor]=useState("")
  useEffect(()=>{
    //setToast(false)
  },[])

  
  

  const updateUser=(dataKey,data)=> 
  {
     //console.log("data key", dataKey) 
    return setUser(prevState => { 
         let newState=({...prevState,[dataKey]: data})
          return newState
        })  
  }

  const addUserApi =()=>{
    setErrors([])
    setErrors(prevErrors=>[...prevErrors,"ready to add user"])
    alert.show('New user created!',{type: 'success'})
//     axios.request( {
//       method: "post", 
//       url: API_URL+"users/add", 
//       data: user
//     }).then (response => {

//     fetchUsersApi()
//       console.log("post response",response.data.data.data.id)
//     }).catch(error => {
//       setErrors([])
//       error.response.status==409 ? setErrors(prevError=>[...prevError,error.response.data.message]) :setErrors(prevError=>[...prevError,error.response.statusText])
//       //console.log("error",error.response)
      
//   });

  }

  

  const fetchUsersApi =()=>{
    setUsers([])
    setFetching(true)

    //alert("Hi there man")

    axios.get( API_URL+"users").then (response => {

      console.log("fetch articles",response.data.data.data.data)
      setUsers(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
      setFetching(false)
      console.log("post response all articles",response.data.data.data)
    }).catch(error => {
      setErrors([])
      setFetching(false)
      error.response!==undefined ? setFetchingFailMsg("No programmes found") : setFetchingFailMsg("Unknown error")
      // let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      // setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const goToPageApi =(page)=>{
    setUsers([])
    setCurrentPage(page)

    axios.get( pagination.path+"?page="+page).then (response => {
        setUsers(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
     
      console.log("post response all articles",response.data.data.data)
    }).catch(error => {
      setErrors([])
      let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const searchByPhraseApi =()=>{
    
    setUsers([])
    setFetching(true)
    setFetchingFailMsg(null)

    axios.get( API_URL+"programmes/search/"+searchPhrase).then (response => {

      
        setUsers(prevArticle=>{
        return [...prevArticle,...response.data.data.data.data]
      })

      setPagination(prevArticle=>{
        return {...prevArticle,...response.data.data.data}
      })
      
      setFetching(false)
      !response.data.data.data.length && setFetchingFailMsg("No results found...")
      console.log("get response",response.data.data.data)
      //console.log("all articles",articles)
    }).catch(error => {
      setErrors([])
      setFetching(false)
     
      error.response!==undefined ? setFetchingFailMsg("No articles found") : setFetchingFailMsg("Unknown error")
      // let apiStatus=error.response!==undefined ? error.response.statusText : "Unknown error"
      // setErrors(prevError=>[...prevError,apiStatus])
      
  });

  }


  const articleContext = {
    user,
    errors,
    users,
    setUser,
    fetchUsersApi,
    pagination,
    goToPageApi,
    currentPage,
    searchPhrase,
    setSearchPhrase,
    searchByPhraseApi,
    fetching,
    fetchingFailMsg,
    updateUser,
    addUserApi,
    toast,
    setToast,
    toastType,
    
  };

  // pass the value in provider and return
  return <Context.Provider value={articleContext}  {...props}></Context.Provider>;
};


const UserProvider= {
    Provider,
    Context
};


export default UserProvider
