import React,{useContext} from "react"
import {
    CToaster,
    CToast,
    CToastHeader,
    CToastBody,
  } from '@coreui/react'
  import ArticleProvider from "../context/ArticleContext"



const ToastMe=({showToast})=>{
    const {toast,toastType,errors,setToast}=useContext(ArticleProvider.Context)

   

    return (
        <>
            <CToaster>
            
                  <CToast
                    style={{background:toastType=="error" ? "green" : "pink",color:toastType=="error" ? "green" : "pink"}}
                    show={showToast}
                    autohide={5000}
                    fade={true}
                    className="success"
                  >
                    <CToastHeader >
                      Error
                    </CToastHeader>
                    <CToastBody>
                        <ul>
                    {
                       errors.length? errors.map((error,i)=> {
                           setToast(true)
                           return (<li key={i}>{error}</li>)
                           }
                           
                           ) :""
                        //errors.length? setToast(true):""  
                           
                    }
                      </ul>
                    </CToastBody>
                  </CToast>
            </CToaster>
         
        </>
      )

}



export default ToastMe
