import React,{useState,useEffect} from 'react'




const ImageUpload= ({postType,target})=>{

    //console.log("target",target)

    const triggerSelectInput=(e,input)=>{
        e.preventDefault()
        input.click()
    }

    useEffect(()=>{
        const uploadBt=document.getElementById(target)
        const upload_input=document.getElementById("upload_input")

        uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,upload_input))
        console.log("use effect reloaded")
    },[])


    console.log("Post type",postType)

    const [imagePreviewUrlEn,setImagePreviewUrlEn]=useState(false)
    const [imagePreviewUrlFn,setImagePreviewUrlFn]=useState(false)
    const [image,setImage]=useState("")

    const onChange=(e)=>{
        //console.log(e.target.value)
        let files = e.target.files || e.dataTransfer.files;
        //alert("on change for loading files")
        if (!files.length)
        return;
        //this.createImage(files[0]);
        let reader = new FileReader();
        reader.onload = (e) => {
        setImage(e.target.result)
        postType=="en" ? setImagePreviewUrlEn(e.target.result) : setImagePreviewUrlFn(e.target.result)
        console.log("image",e.target.result)
        };
        reader.readAsDataURL(files[0]);
    }

    const onFormSubmit=(e)=>{
        //console.log(e.target.value)
    }

    return (<form onSubmit={()=>onFormSubmit}  encType="multipart/form-data" >
       
         {!imagePreviewUrlEn & !imagePreviewUrlFn ?
        <input id="upload_input" type="file" hidden="true" onChange={(e)=>onChange(e)} />:"" }


        <div className="imgPreview">
        { imagePreviewUrlEn ? (<img className="add_imagem img-fluid" Name="add_imagem" src={
            ImagePreviewUrlEn} />) : ( '' )
        }

        { imagePreviewUrlFrn ? (<img className="add_imagem img-fluid" Name="add_imagem" src={
            ImagePreviewUrlFn} />) : ( '' )
        }
        </div>


        </form>)
     
    
}

export default ImageUpload
