import React,{useContext,useEffect,useState} from "react"
import {
    CModal,
    CModalHeader,
    CButton,
    CModalBody,
    CModalFooter,
    CRow,
    CCol
  } from '@coreui/react'
  import ArticleProvider from "../context/ArticleContext"
  import PgCatProvider from "../context/PgCatContext"
  import GalleryProvider from "../context/GalleryContext";
  import axios from "axios";


  const API_URL = "http://192.168.43.122:8080/laravel/site40/public/api/v1/";

  const PUBLIC_URL = "http://192.168.43.122:8080/laravel/site40/public";

  const Styles={
    link:{
      textDecoration:"underline",
      fontSize:"11px",
      color:"green"
    },
    Titles:{
      color:"black"
    },
    Theme:{
      background:"rgb(250, 252, 255)"
    }
  }
    


const ModalMe=({showToast})=>{

  const {featuredFor,setSelectedImageEn,setSelectedImageFr,modal,setModal}=useContext(ArticleProvider.Context)


  const {boy,uploadProgress,setUploadProgress} = useContext(GalleryProvider.Context)

  console.log("boy",uploadProgress)

  //const {answer}=useContext(GalleryProvider.Context)

  const [gallery,updateGallery]=useState([])

  const [selectedImage,setSelectedImage]=useState("")
  const [fileType,setFileType]=useState("")
  const [fileName,setFileName]=useState("")
  const [fileSize,setFileSize]=useState("")
  const [fileDimension,setFileDimension]=useState("")
  const [fileUploadedOn,setFileUploadedOn]=useState("")



  const triggerSelectInput=(e,input)=>{
    e.preventDefault()
     input.click()
}

  useEffect(()=>{
    const uploadBt=document.getElementById("modal-upload-bt")
    const uploadInput=document.getElementById("modal-upload-input")
    uploadBt.addEventListener("click",(e)=>triggerSelectInput(e,uploadInput))
},[])

const onChange=(e)=>{
  //console.log(e.target.value)
  setUploadProgress(0)
  let file=null
  file = e.target.files[0] || e.dataTransfer.files[0];
  e.target.value = "";
  let allowed_mime_types = [ 'image/jpeg', 'image/png' ];
  let allowed_size_mb = 5;
  
  if(allowed_mime_types.indexOf(file.type) == -1) {
alert('Error : Incorrect file type');
return;
}

if(file.size > allowed_size_mb*1024*1024) {
alert('Error : Exceeded size');
return;
}
  //alert("on change for loading files")
  if (!file)
  return;
  //this.createImage(files[0]);
  let reader = new FileReader();
  reader.onload = (e) => {
  //setImage(reader.result)
  //console.log(reader.result)

  };
  reader.readAsDataURL(file);

  let data = new FormData();

  data.append("file",file);
  
  axios.request( {
      method: "post", 
      url: API_URL+"photo/upload", 
      data: data, 
      onUploadProgress: (p) => {
         setUploadProgress(prevProgress=>prevProgress+Math.round((100 * p.loaded) / p.total))
         //console.log("upload progress",p.total/p.loaded)
      }


    }).then (response => {
        console.log("response",response)
        let imgUrlSm=PUBLIC_URL+response.data.data.path+"/"+"small_"+response.data.data.data.file_name+".jpg"

        let imgUrlBg=PUBLIC_URL+response.data.data.path+"/"+"big_"+response.data.data.data.file_name+".jpg"

        console.log("response file path",response.data.data.path)
        console.log("response file sm",imgUrlSm)
        console.log("response file bg",imgUrlBg)

        updateGallery(prevGallery=>[
          ...prevGallery,
          {img_url:response.data.data.path,img_small:imgUrlSm,img_big:imgUrlBg,file_name:response.data.data.data.file_name}
        ],()=>console.log("gallery from api",gallery))

        console.log("gallery from api",gallery)
    })
}


    
    return (
        <>
      
      <CModal
      size="lg"
        show={modal}
        onClose={()=>setModal(!modal)}
      >
        <CModalHeader closeButton><h1>Gallery</h1> </CModalHeader>
        <CModalBody>
          <CRow>
              <CCol md="8">
                {
              <>
            <div className="row">
                
                    <div className="row">
                    {gallery.map((val, k) => {
                        return (
                        <div className="col-md-3" key={k}>
                            <img src={val} className={'img-thumbnail'} onClick={() => {
                    //let imageSource=document.getElementById("modal-selected-image").src.value=val
                              setSelectedImage(val)
                            }} />
                        </div>
                        )
                        })
                    }
                     </div>
                
                
            </div>
            </>
        
                  
              

             
                 
                }

              </CCol>


              <CCol md="4">
              <img  className={'img-fluid'} id="modal-selected-image" src={selectedImage!==""?selectedImage:""} />
              {selectedImage!=="" && <p align="center" id="acpb_ft_rm_fr"><a href="" style={Styles.link} id="acpb_fr_ft" className="acpb_featured_gallery_single_fr_remove">remove</a></p>} 
              {selectedImage!=="" && 
              <>
              <p><b>File name:</b> {fileName}</p>
              <p><b>File type:</b> {fileType}</p>
              <p><b>Uploaded on:</b> {fileUploadedOn}</p>
              <p><b>File size:</b> {fileSize}</p>
              <p><b>Dimensions:</b> {fileDimension}</p>
              </>}
              </CCol>
          </CRow>
          <CRow className="d-flex flex-row justify-content-center">
              {/* <CCol md="8">Left</CCol>
              <CCol md="4">Right</CCol> */}
              
              <CCol md="4">
              <br />
              <br />
              <input id="modal-upload-input" type="file"  hidden={true} onChange={(e)=>onChange(e)} />
              <CButton id="modal-upload-bt" color="primary" size="md" block md="12">Add IMage</CButton>
              ({uploadProgress}% uploaded)
              </CCol>
          </CRow>
        </CModalBody>
        <CModalFooter>
          <CButton color="primary" onClick={()=>{
            
            if(featuredFor!=="") {
              setModal(prev=>!prev)
              console.log("selected Image",selectedImage)
              console.log("Featured For",featuredFor)
              featuredFor=="acpb_en_ft" ? setSelectedImageEn(selectedImage):setSelectedImageFr(selectedImage)
            }
            
          

            
            }}>OK</CButton>{' '}
          <CButton
            color="secondary"
            onClick={()=>setModal(!modal)}
          >Cancel</CButton>
        </CModalFooter>
      </CModal>
    </>
           
      )

}



export default ModalMe
