    link:{
      textDecoration:"underline",
      fontSize:"11px",
      color:"green"
    },
    Titles:{
      color:"black"
    },
    Theme:{
      background:"rgb(250, 252, 255)"
    }
  }
    


const ModalMe=({showToast})=>{

  const {selectedImageGlobal,setSelectedImageGlobal,updateArticle,article,setArticle,featuredFor,setSelectedImageEn,setSelectedImageFr,modal,setModal}=useContext(ArticleProvider.Context)
