import React,{useContext} from "react"
import {
    CToaster,
    CToast,
    CToastHeader,
    CToastBody,
  } from '@coreui/react'
  import ArticleProvider from "../context/ArticleContext"



const ToastMe=({showToast})=>{
    const {errors,setToast}=useContext(ArticleProvider.Context)
    return (
        <>
            <CToaster>
            
                  <CToast
                    style={{background:"pink",color:"wine"}}
                    show={showToast}
                    autohide={"3000"}
                    fade={true}
                    className="success"
                  >
                    <CToastHeader >
                      Toast title
                    </CToastHeader>
                    <CToastBody>
                        <ul>
                    {
                       ()=>{
                        return errors.length ? errors.map(error=>(<li>{error}</li>)):""
                        //errors.length? setToast(true):""  
                       }                              
                         
                           
                    }
                      </ul>
                    </CToastBody>
                  </CToast>
            </CToaster>
         
        </>
      )

}



export default ToastMe
