  if (!file)
  return;
  //this.createImage(files[0]);
  let reader = new FileReader();
  reader.onload = (e) => {
  setImage(reader.result)
  //postType=="en" ? setImagePreviewUrlEn(reader.result) : setImagePreviewUrlFr(reader.result)
  //console.log("image",e.target.result)
  };
  reader.readAsDataURL(file);

  let data = new FormData();
  data.append("file",file);
  //const uploadInputName=postType=="en" ? "upload_input_en" : "upload_input_fr"
  //const uploadInput=document.getElementById(uploadInputName)


// let request = new XMLHttpRequest();
// request.open('POST', API_URL+'photo/upload'); 
// request.addEventListener('load', function(e) {
// 	console.log(request.response.data);
// });
  // request.send(data);
  
  axios.request( {
      method: "post", 
      url: API_URL+"photo/upload", 
      data: data, 
      onUploadProgress: (p) => {
        //console.log(p); 
        //this.setState({
          //fileprogress: p.loaded / p.total
        //})
        //console.log("loaded",p.loaded)
        //console.log("total",p.total)
      }


    }).then (response => {
        console.log("response",response)
      postType=="en" ? setImagePreviewUrlEn(`${PUBLIC_URL+response.data.data.path+"/"+response.data.data.image}`) : setImagePreviewUrlFr(reader.result)
    })
}


    const {modal,setModal}=useContext(ArticleProvider.Context)
    return (
        <>
      
      <CModal
      size="lg"
        show={modal}
        onClose={()=>setModal(!modal)}
      >
        <CModalHeader closeButton>Gallery</CModalHeader>
        <CModalBody>
          <CRow>
              <CCol md="8">Left</CCol>
              <CCol md="4">Right</CCol>
          </CRow>
          <CRow className="d-flex flex-row justify-content-center">
              {/* <CCol md="8">Left</CCol>
              <CCol md="4">Right</CCol> */}
              <img className="img-fluid" src="" />
              <CCol md="4">
              <input id="modal-upload-input" type="file"  hidden={true} onChange={(e)=>{}} />
              <CButton id="modal-upload-bt" color="primary" size="md" block md="12">Add IMage</CButton>
              </CCol>
          </CRow>
        </CModalBody>
        <CModalFooter>
          <CButton color="primary">Do Something</CButton>{' '}
          <CButton
            color="secondary"
            onClick={()=>setModal(!modal)}
          >Cancel</CButton>
        </CModalFooter>
      </CModal>
    </>
           
      )

}



export default ModalMe
