import React, {useEffect, lazy } from 'react'
import {
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import FormikPostEnglish from "../../formik-wrappers/FormikPostEnglish"
import EditorJs from 'react-editor-js';
import Embed from "@editorjs/embed";
import Table from "@editorjs/table";
import List from "@editorjs/list";
import Warning from "@editorjs/warning";
import Code from "@editorjs/code";
import LinkTool from "@editorjs/link";
import Image from "@editorjs/image";
import Raw from "@editorjs/raw";
import Header from "@editorjs/header";
import Quote from "@editorjs/quote";
//import Marker from "@editorjs/marker";
import CheckList from "@editorjs/checklist";
import Delimiter from "@editorjs/delimiter";
import InlineCode from "@editorjs/inline-code";
import SimpleImage from "@editorjs/simple-image";

import MainChartExample from '../charts/MainChartExample.js'

import Paragraph from '../../api-classes/Paragraph'
import RichText from '../../editor/RichText'
import ImageUpload from '../../Uploaders/image-uploader'

const WidgetsDropdown = lazy(() => import('../widgets/WidgetsDropdown.js'))
const WidgetsBrand = lazy(() => import('../widgets/WidgetsBrand.js'))


const Styles={
  link:{
    textDecoration:"underline",
    fontSize:"11px",
    color:"green"
  },
  Titles:{
    color:"black"
  },
  Theme:{
    background:"rgb(250, 252, 255)"
  }
}
  


const DashboardAddPost = () => {

 let editorInstance;

     return (
    <>
      <CRow>
        <CCol md="9">
        <CCard>
        <CCardBody>
          <CCard style={Styles.Theme.background}>
            <CCardHeader>
              English Version
            </CCardHeader>
            <CCardBody>
             <h5>Title </h5>
            <FormikPostEnglish />
            
            <h5>Body </h5>
            <RichText />
            </CCardBody>
          </CCard>

          </CCardBody>
          </CCard>
        </CCol>
        <CCol md="3">
        <CCard>
        <CCardBody>

        <CCard>
        <CCardBody>
        <p><a href="" style={Styles.link}>Set featured image (EN) </a></p>
        <ImageUpload />
                 
        </CCardBody>
          </CCard>

          <CCard>
        <CCardBody>
        <p><a href="" style={Styles.link}>Set featured image (FR) </a></p>
        {/* <hr /> */}
                 
        </CCardBody>
          </CCard>
          <CButton color="primary" size="md" block md="12">Publish</CButton>

          </CCardBody>
          </CCard>
        </CCol>
      </CRow>



      <CRow>
        <CCol md="9">
          <CCard>
            <CCardHeader>
              French
            </CCardHeader>
            <CCardBody>
             <h5>Title (French)</h5>
            <FormikPostEnglish />
            
            <h5>Body (French)</h5>
           
            <RichText />
            </CCardBody>
          </CCard>
        </CCol>
        {/* <CCol md="3">
        <CCard>
        <CCardBody>
        
        </CCardBody>
          </CCard>
        </CCol> */}
      </CRow>



    </>
  )
}

export default DashboardAddPost
