import React,{useContext,useEffect} from 'react'
import UserProvider from "../context/UserContext"
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
    CInputGroup,
    CInputGroupPrepend,
    CInputGroupText,
    CInput,
  } from '@coreui/react'
  import CIcon from '@coreui/icons-react'


const UserRole=()=>{
    const {user,updateUser}=useContext(UserProvider.Context)

    const formik = useFormik({
        initialValues: {
          firstName: '',
          lastName: '',
          email: '',
        },
        validationSchema: Yup.object({
          firstName: Yup.string()
            .max(15, 'Must be 15 characters or less')
            .required('Required'),
          lastName: Yup.string()
            .max(20, 'Must be 20 characters or less')
            .required('Required'),
          email: Yup.string().email('Invalid email address').required('Required'),
        }),
        onSubmit: values => {
          alert(JSON.stringify(values, null, 2));
        },
      });



    return (
    <div className={""}>
        <label>Administrator &nbsp;</label>
       <input  type="radio"  id="statusp" name="status" checked={user.role=="admin"? true:false} value="admin" onClick={(e)=>{
         updateUser("role", e.target.value)
         //console.log("article",article)
         }} />  


         <label>&nbsp;&nbsp;&nbsp;Editor &nbsp;</label>
         <input  type="radio"  id="statusd" name="status" value="editor" checked={user.role=="editor"?true:false} onClick={(e)=>{
         updateUser("role", e.target.value)
         //console.log("article",article)
         }} />

     </div>
   

    )
    

   

}
 export default UserRole
